      priority: "low" as const,
      publishDate: new Date(),
      expiryDate: new Date(Date.now() + 14 * 24 * 60 * 60 * 1000),
    },
  ];

  for (const announcement of announcementData) {
    await db.insert(announcements).values(announcement).onConflictDoNothing();
  }

  // Seed events
  const eventData = [
    {
